  val childOffences: Any? = null,
  val code: String,
  val description: String,
  val endDate: String? = null,
  val homeOfficeStatsCode: String? = null,
  val id: Int,
  val isChild: Boolean,
  val legislation: String? = null,
  val legislationText: String? = null,
  val lineReference: String? = null,
  val loadDate: String? = null,
  val maxPeriodIsLife: Boolean,
  val maxPeriodOfIndictmentDays: Int? = null,
  val maxPeriodOfIndictmentMonths: Int? = null,
  val maxPeriodOfIndictmentWeeks: Int? = null,
  val maxPeriodOfIndictmentYears: Int? = null,
  val offenceType: String,
  val paragraphNumber: Int? = null,
  val paragraphTitle: String? = null,
  val parentOffenceId: Int? = null,
  val revisionId: Int? = null,
  val schedules: List<Any>,
  val startDate: String,
